mapper.select:
select *
from (
		select @rownum:=@rownum+1 as rownum, rno, bno, replytext, replyer, regdate, updatedate
        from (select @rownum:=0) as rownum
        , tbl_reply
        where bno=#{bno}
        order by rno desc
        ) tbl_reply

where rownum > (#{cri.pageNum}-1)*#{cri.amount} and rownum <= #{cri.pageNum}*#{cri.amount};
